import React from "react";

import CONST from "./node.const";

import nodeHelper from "./node.helper";

import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
/**
 * Node component is responsible for encapsulating node render.
 * @example
 * const onClickNode = function(nodeId) {
 *      window.alert('Clicked node', nodeId);
 * };
 *
 * const onRightClickNode = function(nodeId) {
 *      window.alert('Right clicked node', nodeId);
 * }
 *
 * const onMouseOverNode = function(nodeId) {
 *      window.alert('Mouse over node', nodeId);
 * };
 *
 * const onMouseOutNode = function(nodeId) {
 *      window.alert('Mouse out node', nodeId);
 * };
 *
 * <Node
 *     id='nodeId'
 *     cx=22
 *     cy=22
 *     fill='green'
 *     fontSize=10
 *     fontColor='black'
 *     fontWeight='normal'
 *     dx=90
 *     label='label text'
 *     opacity=1
 *     renderLabel=true
 *     size=200
 *     stroke='none'
 *     strokeWidth=1.5
 *     svg='assets/my-svg.svg'
 *     type='square'
 *     viewGenerator=(node) => <CustomComponent node={node} />
 *     className='node'
 *     onClickNode={onClickNode}
 *     onRightClickNode={onRightClickNode}
 *     onMouseOverNode={onMouseOverNode}
 *     onMouseOutNode={onMouseOutNode} />
 */
export default class Node extends React.Component {
  /**
   * Handle click on the node.
   * @returns {undefined}
   */
  handleOnClickNode = () =>
    this.props.onClickNode && this.props.onClickNode(this.props.id);

  /**
   * Handle right click on the node.
   * @param {Object} event - native event.
   * @returns {undefined}
   */
  handleOnRightClickNode = (event) =>
    this.props.onRightClickNode &&
    this.props.onRightClickNode(event, this.props.id);

  /**
   * Handle mouse over node event.
   * @returns {undefined}
   */
  handleOnMouseOverNode = () =>
    this.props.onMouseOverNode && this.props.onMouseOverNode(this.props.id);

  /**
   * Handle mouse out node event.
   * @returns {undefined}
   */
  handleOnMouseOutNode = () =>
    this.props.onMouseOut && this.props.onMouseOut(this.props.id);

  test = () => console.log("TEST");

  render() {
    const nodeProps = {
      cursor: this.props.cursor,
      onClick: this.handleOnClickNode,
      onContextMenu: this.handleOnRightClickNode,
      onMouseOut: this.handleOnMouseOutNode,
      onMouseOver: this.handleOnMouseOverNode,
      opacity: this.props.opacity,
    };

    const textProps = {
      dx:
        this.props.label.length >= 15
          ? -3.2 * this.props.label.length
          : -3.5 * this.props.label.length,
      dy: -25,
      fill: this.props.fontColor,
      fontSize: this.props.fontSize,
      fontWeight: this.props.fontWeight,
      opacity: this.props.opacity,
    };

    const size = this.props.size;

    let gtx = this.props.cx,
      gty = this.props.cy,
      label = null,
      node = null;

    if (this.props.svg || this.props.viewGenerator) {
      const height = size / 10;
      const width = size / 10;
      const tx = width / 2;
      const ty = height / 2;
      const transform = `translate(${tx},${ty})`;

      label = (
        <text {...textProps} transform={transform}>
          {this.props.label}
        </text>
      );

      // By default, if a view generator is set, it takes precedence over any svg image url
      if (this.props.viewGenerator && !this.props.overrideGlobalViewGenerator) {
        node = (
          <svg {...nodeProps} width={width} height={height}>
            <foreignObject x="0" y="0" width="100%" height="100%">
              <section
                style={{ height, width, backgroundColor: "transparent" }}
              >
                {this.props.viewGenerator(this.props)}
              </section>
            </foreignObject>
          </svg>
        );
      } else {
        node = (
          <image
            {...nodeProps}
            href={this.props.svg}
            width={width}
            height={height}
          />
        );
      }

      // svg offset transform regarding svg width/height
      gtx -= tx;
      gty -= ty;
    } else {
      nodeProps.d = nodeHelper.buildSvgSymbol(size, this.props.type);
      nodeProps.fill = this.props.fill;
      nodeProps.stroke = this.props.stroke;
      nodeProps.strokeWidth = this.props.strokeWidth;

      label = <text {...textProps}>{this.props.label}</text>;
      node = <path {...nodeProps} />;
    }

    const gProps = {
      className: this.props.className,
      cx: this.props.cx,
      cy: this.props.cy,
      id: this.props.id,
      transform: `translate(${gtx},${gty})`,
    };

    function handleClick(e, data) {
      console.log(data.foo);
    }

    return (
      <g {...gProps}>
        {node}
        {this.props.renderLabel && label}
      </g>
    );
  }
}
