import React, {useState} from 'react'
import D3Node from '../d3Node/D3Node'
import './LeftWindow.css'

const LeftWindow = () => {

    //set state of Configurations 
    const [myConfig, setMyConfig] = useState({
        nodeHighlightBehavior: true,
        height: window.innerHeight * 0.811,
        width: window.innerWidth,
        node: {
          color: "lightgreen",
          size: 500,
          highlightStrokeColor: "blue",
          symbolType: ""
        },
        link: {
          highlightColor: "lightblue"
        }
        });


           // Change state of Configurations 
    const handleConfig = (color) => {
      setMyConfig({
        nodeHighlightBehavior: true,
        height: window.innerHeight * 0.811,
        width: window.innerWidth,
        node: {
          color: color,
          size: 500,
          highlightStrokeColor: "blue",
        },
        link: {
          highlightColor: "lightblue"
        }
      })
    }

    //const [color, setColor] = useState('')

    const inputHandler = (event) => {
        //prevent the forn submission from refreshing the page
        event.preventDefault() 
        console.log(color)
        handleConfig(color)

        //reset inputs
        //setName('')
    }


    return(
        <div class="leftWindow">
            <form onSubmit={handleConfig}>

            <h5 class="font-weight-light"> Node Color </h5>
            <div class="input-group mb-3">
                <input
                class="color"
                id='color'
                type="text"
                name="nodeColor"
                placeholder={'text'}
                value={myConfig.node.color.target}
                onChange={(e) => setColor(document.getElementById('color').value)}
                //onKeyPress={this._handleLinkKeyEnter}
                />
            </div>


            <input class='submit' type='submit' value='Start'/>
        </form>
        </div>
    )
}

export default LeftWindow





