import React, {useState} from 'react'
import './LeftWindow.css'

const LeftWindow = (props) => {
    const [color, setColor] = useState('')
    const [size, setSize] = useState('')

    const inputHandler = (event) => {
        //prevent the forn submission from refreshing the page
        event.preventDefault() 
        props.color(color)
        props.size(size)

        //reset inputs
        //setName('')
    } 


    return(
        <div class="leftWindow">
            <form onSubmit={inputHandler}>

                <h5 class="font-weight-light"> Node Color </h5>
                <div class="input-group mb-3">
                    <input
                    class="color"
                    id='color'
                    type="text"
                    name="nodeColor"
                    placeholder={'text'}
                    onChange={(e) => setColor(document.getElementById('color').value)}
                    //onKeyPress={this._handleLinkKeyEnter}
                    />
                </div>

                <h5 class="font-weight-light pt-3"> Node Size </h5>
                <div class="input-group mb-3">
                    <input
                    class="linkInput"
                    id='size'
                    type="text"
                    name="nodeSize"
                    placeholder={'text'}
                    onChange={(e) => setSize(document.getElementById('csize').value)}
                    //onKeyPress={}
                    />
                </div>


            <input class='submit' type='submit' value='Start'/>
        </form>
        </div>
    )
}

export default LeftWindow





