import React, {useState} from 'react'
import D3Node from '../d3Node/D3Node'
import './LeftWindow.css'

const LeftWindow = ({handleConfig}) => {

    const [color, setColor] = useState('')

    const inputHandler = (event) => {
        //prevent the forn submission from refreshing the page
        event.preventDefault() 
        console.log(color)
        handleConfig(color)

        //reset inputs
        //setName('')
    }


    return(
        <div class="leftWindow">
            <h5 class="font-weight-light pt-3"> Node Size </h5>
            <div class="input-group mb-3">
                <input
                class="linkInput"
                type="text"
                name="addLink"
                placeholder={this.state.addLinkPlaceholder}
                value={this.state.addLink}
                onChange={this._addLinkHandleChange}
                onKeyPress={this._handleLinkKeyEnter}
                />
            </div>

            <h5 class="font-weight-light"> Node Color </h5>
            <div class="input-group mb-3">
                <input
                class="color"
                id='color'
                type="text"
                name="nodeColor"
                placeholder={this.state.addLinkPlaceholder}
                value={this.state.addLink}
                onChange={(e) => setColor(document.getElementById('color').value)}
                onKeyPress={this._handleLinkKeyEnter}
                />
            </div>




            <select class='color' id='color' onChange={ (e) => setColor(document.getElementById('color').value)}>
                <option value='default'>Color</option>
                <option value='lightgreen'>lightgreen</option>
                <option value='blue'>blue</option>
                <option value='red'>red</option>
                <option value='orange'>orange</option>
                <option value='purple'>purple</option>
                <option value='yellow'>yellow</option>
            </select>



            <input class='submit' type='submit' value=''/>
        </div>
    )
}

export default LeftWindow





