import React from 'react'
import './Node.css'


const Node = () => {

    let nodeElem = document.getElementById('node')
    dragElement(nodeElem); 

    let pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
    const dragElement = elem => {
        document.getElementById(elem.id).onmousedown = dragMouseDown;
    }

    const dragMouseDown = e => {
        e = e || window.event;
        e.preventDefault();
        //set mouse cursor at starting position
        pos3 = e.clientX;
        pos4 = e.clientY;
        document.onmouseup = closeDragElement;
        document.onmousemove = elementDrag;
    }

    const elementDrag = e => {
        e = e || window.event;
        e.preventDefault();
        //calculate new cursor position 
        pos1 = pos3 - e.clientX;
        pos2 = pos4 - e.clientX;
        pos3 = e.clientX;
        pos4 = e.clientY;
        //set new position 
        circleElem.style.top = (document.getElementById('node').offsetTop - pos2) + 'px';
        document.getElementById('node').style.left = (document.getElementById('node').offsetLeft  - pos1) + 'px';
    } 

    const closeDragElement = () => {
        //stop moving when mouse button is released
        document.onmouseup = null;
        document.onmousemove = null;
    }

    return(
        <div className='circle-container'>
            <div id='node' className='circle center shadow'></div>
        </div>
    )
}

export default Node
