import React from 'react'
import './Node.css'


const Node = () => {

    let nodeElem = document.getElementById('container')
    let pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;

    const dragElement = elem => {
        if (document.getElementById(elem.id + 'Node')) {
            document.getElementById(elem.id + 'Node').onmousedown = dragMouseDown;
        }
        else {
            elem.onmousedown = dragMouseDown
        }

    } 

    const dragMouseDown = e => {
        e = e || window.event;
        e.preventDefault();
        //set mouse cursor at starting position
        pos3 = e.clientX;
        pos4 = e.clientY;
        document.onmouseup = closeDragElement;
        document.onmousemove = elementDrag;
    }

    const elementDrag = e => {
        e = e || window.event;
        e.preventDefault();
        //calculate new cursor position 
        pos1 = pos3 - e.clientX;
        pos2 = pos4 - e.clientX;
        pos3 = e.clientX;
        pos4 = e.clientY;
        //set new position 
        nodeElem.style.top = (nodeElem.offsetTop - pos2) + 'px';
        nodeElem.style.left = (nodeElem.offsetLeft  - pos1) + 'px';
    } 

    const closeDragElement = () => {
        //stop moving when mouse button is released
        document.onmouseup = null;
        document.onmousemove = null;
    }

    dragElement(nodeElem);

    return(
        <div id ='container' className='circle-container'>
            <div id='containerNode' className='circle center shadow'></div>
        </div>
    )
}

export default Node
