import React, {useState} from 'react'
import './LeftWindow.css'

const LeftWindow = (props) => {

    //Property States 
    const [nSize, setnSize] = useState('')
    const [nColor, setnColor] = useState('')
    const [lSize, setlSize] = useState('')
    const [lColor, setlColorr] = useState('')

    //Event Handler
    const inputHandler = (event) => {
        //prevent the forn submission from refreshing the page
        event.preventDefault() 

        //Pass states to GraphVisualizer
        props.nSize(nSize) 
        props.nColor(nColor)

        //reset inputs
        //setName('')
    } 


    return(
        <div class="leftWindow">
            <form onSubmit={inputHandler}>

            <h5 class="font-weight-light pt-3"> Node Size </h5>
                <div class="input-group mb-3">
                    <input
                    class="nSize"
                    id='nSize'
                    type="text"
                    name="nodeSize"
                    placeholder={'text'}
                    onChange={(e) => setnSize(document.getElementById('nSize').value)}
                    //onKeyPress={}
                    />
                </div>

                <h5 class="font-weight-light"> Node Color </h5>
                <div class="input-group mb-3">
                    <input
                    class="nColor"
                    id='nColor'
                    type="text"
                    name="nodeColor"
                    placeholder={'text'}
                    onChange={(e) => setnColor(document.getElementById('nColor').value)}
                    //onKeyPress={this._handleLinkKeyEnter}
                    />
                </div>

                <h5 class="font-weight-light"> Link Size </h5>
                <div class="input-group mb-3">
                    <input
                    class="lSize"
                    id='lSize'
                    type="text"
                    name="nodeColor"
                    placeholder={'text'}
                    onChange={(e) => setlSize(document.getElementById('lSize').value)}
                    //onKeyPress={this._handleLinkKeyEnter}
                    />
                </div>

                <h5 class="font-weight-light"> Link Color </h5>
                <div class="input-group mb-3">
                    <input
                    class="lColor"
                    id='lColor'
                    type="text"
                    name="nodeColor"
                    placeholder={'text'}
                    onChange={(e) => setlColor(document.getElementById('lColor').value)}
                    //onKeyPress={this._handleLinkKeyEnter}
                    />
                </div>


            <input class='submit' type='submit' value='Start'/>
        </form>
        </div>
    )
}

export default LeftWindow





